import React, { useEffect, useState } from "react";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import axios from "axios";
import { keyframes } from "@mui/system";
import AOS from "aos";
import "aos/dist/aos.css";

const foldOutAnimation = keyframes`
  0% { transform: scale(1); }
  100% { transform: scale(1.05); }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

export const ExpenseShow = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  useEffect(() => {
    AOS.init({
      duration: 1000,
    });
  }, []);
  useEffect(() => {
    axios
      .get("https://dummyapi.online/api/movies")
      .then((res) => {
        setData(res.data);
        setFilteredData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleSearch = () => {
    if (startDate && endDate) {
      const filtered = data.filter((item) => {
        const itemDate = new Date(item.releaseDate);
        return itemDate >= new Date(startDate) && itemDate <= new Date(endDate);
      });
      setFilteredData(filtered);
    } else {
      setFilteredData(data);
    }
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        marginTop: "5%",
        marginX: "5%",
        backgroundColor: "#f0f8ff",
        padding: "2%",
      }}
    >
      <Typography
        data-aos="fade-down"
        variant="h4"
        component="h1"
        gutterBottom
        sx={{
          textAlign: "center",
        }}
      >
        Daily Expense Tracker
      </Typography>
      <Box
        sx={{ marginBottom: "20px", display: "flex", justifyContent: "center" }}
      >
        <TextField
          label="From Date"
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          InputLabelProps={{
            shrink: true,
          }}
          sx={{ marginRight: "10px" }}
        />
        <TextField
          label="To Date"
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          InputLabelProps={{
            shrink: true,
          }}
        />
        <Button
          variant="contained"
          onClick={handleSearch}
          sx={{ marginLeft: "10px" }}
        >
          Search
        </Button>
      </Box>
      {/* <Grid container spacing={3}>
                {filteredData.map((item) => (
                    <Grid item xs={12} sm={6} md={4} key={item.id}>
                        <Card 
                            sx={{ 
                                minWidth: 275, 
                                boxShadow: 3, 
                                transition: 'transform 0.2s', 
                                backgroundColor: '#ffffff', 
                                '&:hover': { 
                                    animation: `${foldOutAnimation} 0.5s forwards`,
                                    boxShadow: 6, 
                                    backgroundColor: '#7d0a8aS'
                                } 
                            }}
                        >
                            <CardContent>
                                <Typography sx={{ fontSize: 20, fontWeight: 'bold' }} color="text.primary" gutterBottom>
                                    {item.movie}
                                </Typography>
                                <Typography sx={{ mb: 1.5 }} color="text.secondary">
                                    Rating: {item.rating}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    ID: {item.id}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    Release Date: {item.releaseDate} 
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button size="small" variant="contained" color="secondary">Learn More</Button>
                            </CardActions>
                        </Card>
                    </Grid>
                ))}
            </Grid> */}
      <table {...getTableProps()}>
        {" "}
        <thead>
          {" "}
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {" "}
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}{" "}
            </tr>
          ))}{" "}
        </thead>{" "}
        <tbody {...getTableBodyProps()}>
          {" "}
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {" "}
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}{" "}
              </tr>
            );
          })}{" "}
        </tbody>{" "}
      </table>
    </Box>
  );
};
